import "./App.css";
import "./index.css";
import { pizzaData } from "./data.js";
/*import pizza1 from "./pizzas/focaccia.jpg";
import pizza2 from "./pizzas/spinaci.jpg";
import pizza3 from "./pizzas/salamino.jpg";
import pizza4 from "./pizzas/funghi.jpg";
import pizza5 from "./pizzas/margherita.jpg";
import pizza6 from "./pizzas/prosciutto.jpg";*/
const publicPath = `${process.env.PUBLIC_URL}`;

function App() {
  return (
    <div className="container">
      <Header></Header>
      <Menu></Menu>
      <Footer></Footer>
      <Order></Order>
    </div>
  );
}

export default App;

function Header() {
  return (
    <div className="header">
      <h1>Fast react pizza Co.</h1>
    </div>
  );
}
function Footer() {
  const date = new Date();
  const currentTime = date.getHours() * 100 + date.getMinutes();

  const isOpen = currentTime >= 1200 && currentTime <= 2200;

  const showTime = date.toLocaleTimeString([], {
    hour: "2-digit",
    minute: "2-digit",
    second: "2-digit",
  });

  return (
    <div>
      <h1 className="footer">
        It is currently {showTime}. We're {isOpen ? "open" : "closed"}. Come
        visit us or order online.
      </h1>
    </div>
  );
}
function Menu() {
  return (
    <div className="menu">
      <h2>OUR MENU</h2>
      <p>
        Authentic Italian cousine. 6 creative dishes choose from. All from our
        store oven, all organic delicious.{" "}
      </p>
      <div className="pizzas">
        <Pizza i={0} />
        <Pizza i={1} />
        <Pizza i={2} />
        <Pizza i={3} />
        <Pizza i={4} />
        <Pizza i={5} />
      </div>
    </div>
  );
}
const Pizza = (props) => {
  return (
    <div className="pizza">
      <img
        src={"".concat(publicPath, pizzaData[props.i].photoName)}
        alt={pizzaData[props.i].name}
      ></img>
      <div className="pizza">
        <h3>{pizzaData[props.i].name}</h3>
        <p>{pizzaData[props.i].ingredients}</p>
        <span>{pizzaData[props.i].price}</span>
      </div>
    </div>
  );
};
function Pizza2() {
  return (
    <div className="pizza">
      <img
        src={"".concat(publicPath, pizzaData[1].photoName)}
        alt={pizzaData[1].name}
      ></img>
      <div className="pizza">
        <h3>Pizza Spinaci</h3>
        <p>Tomato, mozarella, spinach, adn ricotta cheese.</p>
        <span>10</span>
      </div>
    </div>
  );
}
function Pizza3() {
  return (
    <div className="pizza">
      <img src="" alt="pizza"></img>
      <div className="pizza">
        <h3>Pizza Salamino</h3>
        <p>Tomato, moarella, and pepperoni.</p>
        <span>15</span>
      </div>
    </div>
  );
}
function Pizza4() {
  return (
    <div className="pizza">
      <img src="" alt="pizza"></img>
      <div className="pizza">
        <h3>Pizza Funghi</h3>
        <p>Tomato, mozzarella, mushrooms, and onion.</p>
        <span>8</span>
      </div>
    </div>
  );
}
function Pizza5() {
  return (
    <div className="pizza">
      <img src="" alt="pizza"></img>
      <div className="pizza">
        <h3>Pizza Margherita</h3>
        <p>Tomato and mozarella.</p>
        <span>6</span>
      </div>
    </div>
  );
}
function Pizza6() {
  return (
    <div className="pizza">
      <img src="" alt="pizza"></img>
      <div className="pizza">
        <h3>Pizza Prosciutto</h3>
        <p>Tomato, mozarella, aragula, and burratta cheese.</p>
        <span>4</span>
      </div>
    </div>
  );
}

function Order() {
  return <button className="btn">Order</button>;
}
